CREATE TABLE horo_amulets (
  id serial NOT NULL,
  upamulet varchar(50) NOT NULL,
  amulet varchar(50) NOT NULL,
  image_id integer DEFAULT NULL,
  "type" smallint NOT NULL,
  content text NOT NULL,
  PRIMARY KEY (id)
);
CREATE UNIQUE INDEX horo_amulets_upamulet ON horo_amulets (upamulet);
CREATE INDEX horo_amulets_type ON horo_amulets ("type");


