CREATE TABLE horo_dreams_v2 (
  id serial NOT NULL,
  updream varchar(50) DEFAULT NULL,
  dream varchar(50) DEFAULT NULL,
  "type" smallint NOT NULL,
  content text NOT NULL,
  PRIMARY KEY (id)
);
CREATE UNIQUE INDEX horo_dreams_v2_updream ON horo_dreams_v2 (updream);
CREATE INDEX horo_dreams_v2_type ON horo_dreams_v2 (type);


