CREATE TABLE horo_druids (
  id serial NOT NULL,
  druid varchar(50) NOT NULL,
  updruid varchar(50) NOT NULL,
  icon_id integer NOT NULL,
  period varchar(100) NOT NULL,
  content text NOT NULL,
  "sort" integer NOT NULL,
  PRIMARY KEY (id)
);
CREATE UNIQUE INDEX horo_druids_updruid ON horo_druids (updruid);
CREATE INDEX horo_druids_order ON horo_druids ("sort");


