CREATE TABLE horo_flowers (
  id serial NOT NULL,
  flower varchar(50) DEFAULT NULL,
  upflower varchar(50) NOT NULL,
  icon_id integer NOT NULL,
  period varchar(50) NOT NULL,
  content text,
  "order" integer NOT NULL,
  PRIMARY KEY (id)
);
CREATE UNIQUE INDEX horo_flowers_upflower ON horo_flowers (upflower);
CREATE INDEX horo_flowers_order ON horo_flowers ("order");


