CREATE TABLE horo_japans (
  id serial NOT NULL,
  japan varchar(50) NOT NULL,
  upjapan varchar(50) NOT NULL,
  icon_id integer NOT NULL,
  period varchar(100) NOT NULL,
  content text NOT NULL,
  "sort" integer NOT NULL,
  PRIMARY KEY (id)
);
CREATE UNIQUE INDEX horo_japans_upjapan ON horo_japans (upjapan);
CREATE INDEX horo_japans_order ON horo_japans ("sort");


INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Обезьяна', 'ОБЕЗЬЯНА', 248, '1908, 1920, 1932, 1944, 1956, 1968, 1980, 1992, 2004, 2016', 'Это самые ненадежные и противоречивые люди. Умны, ловки, изобретательны, оригинальны и легко решают самые сложные проблемы. Почти нет поля деятельности, где бы они не смогли развернуться. Однако их легко отвлечь и переубедить. Они хотят все совершить тут же, сию минуту. Самое легкое препятствие способно испортить им настроение, сбить их планы. Многие из них отличаются нерешительностью. Это темпераментные и своевольные люди, вспыльчивые, неотходчивые. Умеют принимать решения, обладают здравым смыслом. Многие из них могут добиться славы, если им в этом не мешают.\r\n\r\nВам идеально подходят как друзья или спутники жизни Дракон и Крыса.\r\n\r\nВ большей или меньшей степени Вам подходят Кролик, Коза, Собака, Обезьяна.\r\n\r\nАбсолютно противопоказаны, решительно не подходят Змея, Кабан, Тигр.', 1);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Собака', 'СОБАКА', 251, '1910, 1922, 1934, 1946, 1958, 1970, 1982, 1994, 2006, 2018', 'Эти люди обладают лучшими человеческими чертами - верны, честны, внушают доверие тем, что могут хранить тайны. Однако несколько эгоистичны, эксцентричны и невероятно упрямы. К богатству не стремятся, но деньги у них всегда водятся. Могут быть эмоционально холодными малообщительными. Смотрят на много критически, славясь острым языком. Знают, за что борются, стоят всегда за справедливость. Доводят дело до конца и обычно побеждают. Из них получаются прекрасные руководители.\r\n\r\nВам идеально подходят как друзья или спутники жизни Лошадь, Тигр, Кролик.\r\n\r\nВ большей или меньшей степени Вам подходят Крыса, Змея, Обезьяна, Собака, Кабан.\r\n\r\nАбсолютно противопоказаны, решительно не подходят Дракон, Коза.', 3);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Крыса', 'КРЫСА', 318, '1900, 1912, 1924, 1936, 1948, 1960, 1972, 1984, 1996, 2008, 2020', 'Рожденные под этим знаком обладают приятной внешностью, привлекательны, целеустремленны, трудолюбивы, стремятся к приобретению богатств. Бережливы, любят экономить деньги. Забыть о бережливости они могут лишь при увлеченности или большом чувстве. Щедры только с теми, кого любят. Аккуратны до педантичности. Во многом нелогичны. Честолюбивы. Как правило, преуспевают и добиваются своего. Не умеют сохранять внешнее самообладание, легко впадают в гнев. Обычно честны и открыты, но многие из них не прочь посплетничать.\r\n\r\nВам идеально подходят как друзья или спутники жизни Дракон, Обезьяна, Вол.\r\n\r\nВ большей или меньшей степени Вам подходят Змея, Тигр, Собака, Кабан, Крыса.\r\n\r\nАбсолютно противопоказаны, решительно не подходит Лошадь.', 5);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Тигр', 'ТИГР', 319, '1902, 1914, 1926, 1938, 1950, 1962, 1974, 1986, 1998, 2010', 'Рожденные под этим знаком обладают приятной внешностью, привлекательны, целеустремленны, трудолюбивы, стремятся к приобретению богатств. Бережливы, любят экономить деньги. Забыть о бережливости они могут лишь при увлеченности или большом чувстве. Щедры только с теми, кого любят. Аккуратны до педантичности. Во многом нелогичны. Честолюбивы. Как правило, преуспевают и добиваются своего. Не умеют сохранять внешнее самообладание, легко впадают в гнев. Обычно честны и открыты, но многие из них не прочь посплетничать.\r\n\r\nВам идеально подходят как друзья или спутники жизни Дракон, Обезьяна, Вол.\r\n\r\nВ большей или меньшей степени Вам подходят Змея, Тигр, Собака, Кабан, Крыса.\r\n\r\nАбсолютно противопоказаны, решительно не подходит Лошадь.', 7);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Дракон', 'ДРАКОН', 320, '1904, 1916, 1928, 1940, 1952, 1964, 1976, 1988, 2000, 2012', 'Люди этого знака обладают прекрасным здоровьем, энергичны, легко возбудимы, иногда упрямы и резки. Честны, эмоциональны, решительны, на них можно положиться. Отличаются своеволием. Откровенны и их мнение всегда обосновано. Они способны, не любят занимать деньги и произносить речи. Склонны к мягкосердечию и часто позволяют другим взять над собой верх, но только ненадолго. Они быстро избавляются от своих заблуждений. Женятся или выходят замуж в ранней молодости или не делают этого вообще. Пользуются любовью окружающих.\r\n\r\nВам идеально подходят как друзья или спутники жизни Крыса, Змея, Обезьяна, Петух.\r\n\r\nВ большей или меньшей степени Вам подходят Тигр, Лошадь, Коза, Кабан, Дракон.\r\n\r\nАбсолютно противопоказаны, решительно не подходит Собака.', 9);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Лошадь', 'ЛОШАДЬ', 321, '1906, 1918, 1930, 1942, 1954, 1966, 1978, 1990, 2002, 2014', 'Эти люди очень популярны. Обладают веселым характером, умеют обращаться с деньгами. Умны, проницательны, хотя иногда и много говорят. Талантливы, все у них ладится, обращают на себя внимание броской одеждой или непринужденным поведением. Самоуверенны, знают себе цену и неравнодушны (мужчины) к женщинам. Способны забыть обо всем, когда затронуты их чувства. Во всем, что их касается, вкладывают максимум силы и страсти, за исключением служебных обязанностей. Любят развлечения и большие сборища, обожают быть в центре событий. Очень независимы и редко прислушиваются к советам, поступая при этом по собственному усмотрению. Вам идеально подходят как друзья или спутники жизни Тигр, Собака, Коза.\r\n\r\nВ большей или меньшей степени Вам подходят Дракон, Змея, Обезьяна, Петух, Кабан.\r\n\r\nАбсолютно противопоказаны, решительно не подходит Крыса.', 11);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Петух', 'ПЕТУХ', 322, '1909, 1921, 1933, 1945, 1957, 1969, 1981, 1993, 2005, 2017', 'Глубокие мыслители и одаренные личности. Любят труд и преданы своей работе. Стараются выполнить любое обязательство, которое им даже не под силу, и очень огорчаются при неудаче. Порой несколько эксцентричны, не сразу находят контакт с другими. Всегда уверены в своей правоте, и порой действительно бывают правы. Часто эти люди одиноки и замкнуты. Хотя они и могут производить впечатление решительных, но по натуре робки. Планы, которые они вынашивают, никогда не реализуются. У них бывают резкие подъемы и также резкие спады чувств. В жизни полосы удач чередуются с полосами неудач. Они могут быть эгоистичными, но чересчур прямодушны и способны на отчаянные и смелые действия. Всегда интересны.\r\n\r\nВам идеально подходят как друзья или спутники жизни Вол, Змея, Дракон.\r\n\r\nВ большей или меньшей степени Вам подходят Крыса, Петух, Собака, Кролик.\r\n\r\nАбсолютно противопоказаны, решительно не подходят Тигр, Лошадь, Обезьяна, Коза, Кабан.', 2);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Кабан', 'КАБАН', 323, '1911, 1923, 1935, 1947, 1959, 1971, 1983, 1995, 2007, 2019', 'Этих людей отличают храбрость и способность к самопожертвованию. Все, за что берутся, делают с полной отдачей сил. Признают только прямой путь, не зная отступлений. Очень честные и мужественные люди. С другими людьми сходятся с трудом, но своим немногочисленным друзьям верны до конца жизни, не оставляют друзей в беде. Немногословны, но очень любознательны, много читают, хорошо информированы. Бывают вспыльчивы, но не любят ссор и пререканий. Добры и внимательны к любимым и знакомым, хотя их семейные дела и не всегда идут гладко. Стремятся ликвидировать все конфликты и не допускать длительных размолвок. Какие бы сложные проблемы перед ними не вставали - они никогда не опускают рук, хотя и действуют под влиянием сиюминутного порыва.\r\n\r\nВам идеально подходят как друзья или спутники жизни Кролик, Коза.\r\n\r\nВ большей или меньшей степени Вам подходят Крыса, Тигр, Вол, Дракон, Петух, Собака, Кабан.\r\n\r\nАбсолютно противопоказаны, решительно не подходит Змея.', 4);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Вол', 'ВОЛ', 324, '1901, 1913, 1925, 1937, 1949, 1961, 1973, 1985, 1997, 2009', 'Эти люди терпеливы, немногословны, внушают доверие. Однако порой они могут быть и эксцентричными, легко выходят из себя. В эти моменты их стоит остерегаться - ярость Вола не знает границ. Обычно неразговорчивые, в момент увлечений бывают красноречивы. Обладают превосходными умственными и физическими данными. Слывут людьми легкого характера, но при этом часто проявляют упрямство - не любят противоречий. Некоторые из них смотрят на любовь как на спорт, что ведет к недоразумениям с близкими.\r\n\r\nВам идеально подходят как друзья или спутники жизни Змея, Петух, Крыса.\r\n\r\nВ большей или меньшей степени Вам подходят Дракон, Кролик, Обезьяна, Кабан, Вол.\r\n\r\nАбсолютно противопоказаны, решительно не подходит Коза.', 6);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Кролик', 'КРОЛИК', 325, '1903, 1915, 1927, 1939, 1951, 1963, 1975, 1987, 1999, 2011', 'Он обладает яркой индивидуальностью, талантлив и честолюбив. Добродетелен, сдержан, отличается безукоризненным вкусом. Вызывает общее восхищение и доверие. Материальное состояние складывается удачно. Эти люди любят иногда посплетничать, но при этом тактичны и не злы. Нежны к тем, кого любят, но к ближайшим родственникам редко привязаны. Почти никогда не выходят из себя, обладают прекрасными деловыми качествами. Добросовестны и обязательны, хотя иногда бывают педантичны, иногда проявляют склонность к меланхолии. Из них могли бы выйти отличные игроки, но они играют редко, будучи людьми консервативными, осмотрительными.\r\n\r\nВам идеально подходят как друзья или спутники жизни Коза, Кабан, Собака.\r\n\r\nВ большей или меньшей степени Вам подходят Крыса, Кролик, Дракон.\r\n\r\nАбсолютно противопоказаны, решительно не подходит Петух.', 8);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Змея', 'ЗМЕЯ', 326, '1905, 1917, 1929, 1941, 1953, 1965, 1977, 1989, 2001, 2013', 'Это люди сложные, от рождения наделены мудростью, неразговорчивы. Их дела всегда идут прекрасно, но они часто скуповаты. Иногда эгоистичны и тщеславны. Однако могут проявлять активное участие в своих менее удачливых собратьях. Часто перегибают палку, не веря суждениям других, полагаясь только на себя. Решительные и целеустремленные характеры, остро переживающие свои неудачи. Внешне спокойные, но страстные натуры. Обычно отличаются привлекательностью внешней и внутренней, что при некоторой их ветрености ведет к семейным осложнениям. Вам идеально подходят как друзья или спутники жизни Вол, Петух.\r\n\r\nВ большей или меньшей степени Вам подходят Крыса, Кролик, Дракон, Змея, Лошадь, Коза.\r\n\r\nАбсолютно противопоказаны, решительно не подходят Тигр, Кабан.', 10);
INSERT INTO horo_japans (japan, upjapan, icon_id, period, content, "sort") VALUES ('Коза', 'КОЗА', 327, '1907, 1919, 1931, 1943, 1955, 1967, 1979, 1991, 2003, 2015', 'Люди этого знака наделены способностями в области изящных искусств. На первый взгляд, у них все оказывается удачнее, чем у других. Но часто бывают беспомощны перед жизнью, застенчивы и склонны к пессимизму, нерешительны, затрудняются в принятии самостоятельных решений. Обычно они религиозны. Вожди из них выходят редко. Они не отличаются красноречием, но убеждения свои отстаивают и любят свое дело. Денег обычно имеют достаточно, ценят удобства, с ними связанные. Рассудительны, мягки, доброжелательны, обладают хорошим вкусом. Вам идеально подходят как друзья или спутники жизни Кролик, Кабан, Лошадь.\r\n\r\nВ большей или меньшей степени Вам подходят Тигр, Дракон, Змея, Коза.\r\n\r\nАбсолютно противопоказаны, решительно не подходят Вол, Собака.', 12);
