CREATE TABLE horo_names_v2 (
  id serial NOT NULL,
  upname varchar(50) DEFAULT NULL,
  "name" varchar(50) DEFAULT NULL,
  sex smallint NOT NULL,
  content text NOT NULL,
  PRIMARY KEY (id)
);
CREATE UNIQUE INDEX horo_names_v2_upname ON horo_names_v2 (upname);
CREATE INDEX horo_names_v2_sex ON horo_names_v2 (sex);


